mapper.insert:
insert into commute_correction (
	   crct_cd
	 , commute_cd
	 , crct_go_time
	 , crct_leave_time
	 , crct_reason
	 , atch_file_id
	 , create_id
	 , create_dt
	 , sign_id
)
values (
	   seq_crct_cd.nextval
	 , #{commuteCd}
	 , to_date(#{crctGoTime}, 'YYYY-MM-DD HH24:MI:SS')
	 , to_date(#{crctLeaveTime}, 'YYYY-MM-DD HH24:MI:SS')
	 , #{crctReason}
	 , #{atchFileId}
	 , #{createId}
	 , sysdate
	 , #{signId}
)

mapper.select:
select crct_cd , commute_cd , crct_go_time , crct_leave_time , crct_reason , atch_file_id , create_id , create_dt , sign_id , sign_dt , sign_state from commute_correction where create_id = #{createId}
and to_char(commute_dt, 'yyyy-mm-dd') >= #{startDate}
and to_char(commute_dt, 'yyyy-mm-dd') <= #{endDate}
order by create_dt desc
