mapper.select:
SELECT * FROM ( SELECT T0.*, ROWNUM RNUM FROM (
select pr_cd, --프로젝트 코드 type_cd, --프로젝트 구분코드 pr_nm, --프로젝트명 ahead_dt, --내부완료일 start_dt, --시작일 end_dt, --종료일 price, --금액 create_id, create_dt, entrprs_mber_id, --거래처id state, --진행상태 fn_get_comnm(entrprs_mber_id) as com_nm, --거래처명 (SELECT LISTAGG(fn_get_memnm(mber_id), ', ') WITHIN GROUP (ORDER BY mber_id) FROM project_mem WHERE pr_cd = project.pr_cd) as mem_arr from project where UPPER(del_yn) = UPPER('N')
order by create_dt desc
) T0 ) WHERE RNUM BETWEEN #{firstIndex} + 1 AND #{firstIndex} + #{recordCountPerPage}

mapper.update:
	    UPDATE project_mem
		SET mgr_st = CASE 
             WHEN pr_cd = #{prCd} AND mber_id = #{mberId} THEN #{mgrSt} 
                WHEN pr_cd = #{prCd} AND mber_id <> #{mberId} THEN 'A02'  
             END
		WHERE pr_cd = #{prCd}
