mapper.select:
SELECT * FROM ( SELECT T0.*, ROWNUM RNUM FROM (
select pr_cd, --프로젝트 코드 type_cd, --프로젝트 구분코드 pr_nm, --프로젝트명 ahead_dt, --내부완료일 start_dt, --시작일 end_dt, --종료일 price, --금액 create_id, create_dt, entrprs_mber_id, --거래처id state, --진행상태 fn_get_comnm(entrprs_mber_id) as com_nm --거래처명 from project where UPPER(del_yn) = UPPER('N')
order by create_dt desc
) T0 ) WHERE RNUM BETWEEN #{firstIndex} + 1 AND #{firstIndex} + #{recordCountPerPage}
