mapper.insert:
insert into vacation (
	   vc_cd
	 , vc_start_dt
	 , vc_end_dt
	 , vc_type
	 , use_days
	 , vc_reason
	 , atch_file_id
	 , create_id
	 , create_dt
	 , sign_id
)
values (
	   seq_vc_cd.nextval
	 , #{vcStartDt}
	 , #{vcEndDt}
	 , #{vcType}
	 , #{useDays}
	 , #{vcReason}
	 , #{atchFileId}
	 , #{createId}
	 , sysdate
	 , #{signId}
)

mapper.update:
update vacation
set vc_start_dt = #{vcStartDt}
  , vc_end_dt = #{vcEndDt}
  , vc_type = #{vcType}
  , use_days = #{useDays}
  , vc_reason = #{vcReason}
  , atch_file_id = #{atchFileId}
  , update_id = #{updateId}
  , update_dt = sysdate
  , sign_id = #{signId}
where vc_cd = #{vcCd}

update vacation
set del_yn = 'Y'
where vc_cd = #{vcCd}

mapper.select:
SELECT * FROM ( SELECT T0.*, ROWNUM RNUM FROM (
select vc_cd , vc_start_dt , vc_end_dt , vc_type , use_days , vc_reason , atch_file_id , create_id , fn_get_memnm(create_id) create_nm , create_dt , sign_id , fn_get_memnm(sign_id) sign_nm , sign_dt , sign_state from vacation where create_id = #{createId} and del_yn = 'N'
and vc_type = #{vcType}
and sign_state = #{signState}
and create_dt >= to_date(#{startDate}, 'yyyy-mm-dd')
and create_dt < to_date(#{endDate}, 'yyyy-mm-dd') + 1
order by create_dt desc
) T0 ) WHERE RNUM BETWEEN #{firstIndex} + 1 AND #{firstIndex} + #{recordCountPerPage}

select count(vc_cd) as cnt
from vacation
where create_id = #{createId}
and del_yn = 'N'

	and vc_type = #{vcType}


	and sign_state = #{signState}



	and create_dt >= to_date(#{startDate}, 'yyyy-mm-dd')




	and create_dt < to_date(#{endDate}, 'yyyy-mm-dd') + 1


order by create_dt desc

select count(case when sign_state = 'D01' then 1 end) as signWait, sum(case when sign_state = 'D01' then use_days else 0 end) as signWaitDays, count(case when sign_state = 'D03' then 1 end) as signSup, sum(case when sign_state = 'D03' then use_days else 0 end) as signSupDays from vacation where create_id = #{createId}
and create_dt >= to_date(#{createDt} || '-01-01', 'yyyy-mm-dd') and create_dt < to_date((TO_NUMBER(#{createDt}) + 1) || '-01-01', 'yyyy-mm-dd')
and del_yn = 'N'

SELECT * FROM ( SELECT T0.*, ROWNUM RNUM FROM (
select vc_cd , vc_start_dt , vc_end_dt , vc_type , use_days , vc_reason , atch_file_id , create_id , fn_get_memnm(create_id) create_nm , create_dt , sign_id , fn_get_memnm(sign_id) sign_nm , sign_dt , sign_state from vacation where sign_id = #{signId} and sign_state in ('D01', 'D03') and del_yn = 'N'
and create_dt >= to_date(#{startDate}, 'yyyy-mm-dd')
and create_dt < to_date(#{endDate}, 'yyyy-mm-dd') + 1
order by create_dt
) T0 ) WHERE RNUM BETWEEN #{firstIndex} + 1 AND #{firstIndex} + #{recordCountPerPage}

SELECT * FROM ( SELECT T0.*, ROWNUM RNUM FROM (
select vc_cd , vc_start_dt , vc_end_dt , vc_type , use_days , vc_reason , atch_file_id , create_id , fn_get_memnm(create_id) create_nm , create_dt , sign_id , fn_get_memnm(sign_id) sign_nm , sign_dt , sign_state from vacation where sign_id = #{signId} and sign_state not in ('D01', 'D03') and del_yn = 'N'
and sign_dt >= to_date(#{startDate}, 'yyyy-mm-dd')
and sign_dt < to_date(#{endDate}, 'yyyy-mm-dd') + 1
order by create_dt desc
) T0 ) WHERE RNUM BETWEEN #{firstIndex} + 1 AND #{firstIndex} + #{recordCountPerPage}

mapper.update:
update vacation
set sign_state = #{signState}
  , sign_dt = sysdate
where vc_cd = #{vcCd}
