mapper.insert:
insert into vacation (
	   vc_cd
	 , vc_start_dt
	 , vc_end_dt
	 , vc_type
	 , use_days
	 , vc_reason
	 , atch_file_id
	 , create_id
	 , create_dt
	 , sign_id
)
values (
	   seq_vc_cd.nextval
	 , #{vcStartDt}
	 , #{vcEndDt}
	 , #{vcType}
	 , #{useDays}
	 , #{vcReason}
	 , #{atchFileId}
	 , #{createId}
	 , sysdate
	 , #{signId}
)

mapper.update:
update vacation
set vc_start_dt = #{vcStartDt}
  , vc_end_dt = #{vcEndDt}
  , vc_type = #{vcType}
  , use_days = #{useDays}
  , vc_reason = #{vcReason}
  , atch_file_id = #{atchFileId}
  , update_id = #{updateId}
  , update_dt = sysdate
  , sign_id = #{signId}
where vc_cd = #{vcCd}

update vacation
set del_yn = 'Y'
where vc_cd = #{vcCd}
